variant.equation:
return { (a,N,N) }
else:
nodes = {}
for each (L,R) in N.choices:
left-nodes <- forest-composition(L, a)
for each (a,L,b) in left-nodes:
right-nodes <- forest-composition(R, b)
for each (b,R,c) in right-nodes:
new-n = (a,N,c)
nodes <- nodes + { new-n }
new-n.choices <- new-n.choices + U(a,L,b), (b,R,c))]
return nodes
