variant.listItem:
1: func build automaton;
2: R ← Q;
3: if (fanin(q0) > 0) then
4: q0 ← clone(q0);
5: fi;

6: w' ← c;
7: while ((w ← nextword) =~ c) do
8: p ← lcp(w,w');
9: M ← minim path(M,w',p);
10: M ← add suffix(M, w, p);
11: w' ← w;
12: end;
13: minim path(M,w',q0);
14: if ∃r ∈ R : equiv(r,q0) →
15: delete q0; q0 ← r;
16: fi;
17: cnuf
18: func lcp(M, w, w');
19: j ← max(i : ∀k<j wk = wk );
20: return w1 ... wj;
21: cnuf
22: func minim path(M, w, p);
23: q ← 6*(q0, p);
24: i ← |p|;j ← i;
25: while i ≤ |w |do
26: path[i − j] ← q;
27: q ← 6(q,wi); i ← i + 1;
28: end;
29: path[i − j] ← q;
30: while i > j do
31: if ∃r ∈ R : equiv(r, q) then
32: 6(path[i − j − 1],wi_1) ← r;
33: delete q;
34: else
35: R ← R ∪ {q};
36: fi;
37: i ← i − 1;
38: end;
39: return M;
40: cnuf
41: func add suffix(M,w,p);
42: q ← 6*(q0,p);
43: i ← |p |+ 1;
44: while i ≤ |w |and 6(q,wi) =� ⊥ and fanin(6(q,wi)) ≤ 1 do
45: q ← 6(q,wi); R ← R − {q}; i ← i + 1;
46: end;
47: while i ≤ |w |and 6(q,wi) =� ⊥ do
48: 6(q,wi) ← clone(6(q,wi));
49: q ← 6(q,wi); i ← i + 1;
50: end;
51: while i < |w |do
52: 6(q,wi) ← newstate;
53: q ← 6(q,wi); i ← i + 1;

variant.figure:
Computational Linguistics Volume 30, Number 2
54: end;
55: F ← F U {q};
56: return M;
57: cnuf

variant.listItem:
1. the language of the automaton after the addition of the string contains
that string;
2. no other strings are added to the automaton;
3. no strings are removed from the automaton;
4. the automaton remains minimal except for the path of the newly added
string, that is, the states covered by the path of the newly added string
are representatives of the only equivalence classes that may have more
than one member.
